      <div className="App"style={{minHeight: "99vh"}} >
        <h1>Pokemon Names </h1>
        <form>
          <input
          type='text'
            placeholder="Search List for Pokemon"
            onChange={this.search}
          />
{loading}
{filterPokemon}
        </form>
      </div>
    );
  }
}
